        // 这个时候需要获取用户diy的单词
        function getAllWords(str) {
            var firstLetter = str.slice(0, 1).toLowerCase();
            if (/[a-z]/i.test(firstLetter)) {
                // 自定义词典和默认词典混合排序
                var commondict = storageModel("get", "commondict")[firstLetter];
                let tempDiyDict = storageModel("get", `diydict${book_id}`);
                let tempDiyCommon = storageModel("get", `diydictcommon${book_id}`);

                var thatWordList = [...dict[firstLetter]];
                if (tempDiyDict && tempDiyCommon && tempDiyDict[firstLetter] && tempDiyCommon[firstLetter]) {
                    thatWordList = tempDiyDict[firstLetter].concat(thatWordList);
                    commondict = tempDiyCommon[firstLetter].concat(commondict);
                }
                // 常用词排序
                thatWordList.sort((a, b) => {
                    var res = commondict[thatWordList.indexOf(b)] - commondict[thatWordList.indexOf(a)]
                    return res;
                })

                if (thatWordList && thatWordList.length > 0) {
                    var checkWord = thatWordList.filter((item) => new RegExp(`^${str}.*`, "i").test(item));
                    return checkWord;
                } else {
                    return [];
                }
            } else {
                return [];
            }
        }
